build-gradle/g-build-variants.html.body:
android{
  buildTypes {
         release {
         }

         debug {
         }
  }
  //api的配置比mode有更高的优先级
  flavorDimensions "api", "mode"
  productFlavors {
    demo {
      dimension "mode"
    }
    full {
      dimension "mode"
    }

    minApi24 {
      dimension "api"
    }
    minApi23 {
      dimension "api"
    }
